import React, { useState } from "react";
import styled from "styled-components";
import Map from "./Map";
import "./assets/Map.css";

const Location = () => {
  const [openMap, setOpenMap] = useState(false);

  const onToggle = () => {
    setOpenMap(!openMap);
  };
  return (
    <Background>
      <Container>
        <Title>
          <h5>here</h5>
        </Title>
        <h3>안녕하세요!</h3>
        <h2>내 위치를 찾아볼까요?</h2>
        <Emoji>💁</Emoji>
        <Button onClick={onToggle}>Get started!</Button>
        {openMap && <Map />}
      </Container>
    </Background>
  );
};

const Background = styled.div`
  border: 1px solid red;
  width: 414px;
  height: 696px;
  margin: 30px;
`;

const Container = styled.div`
  border: 1px solid black;
  margin: 30px;
`;

const Title = styled.div`
  margin-bottom: 40px;
  font-size: 30px;
`;

const Emoji = styled.div`
  font-size: 100px;
`;

const Button = styled.button`
  background: #44884b;
  width: 180px;
  height: 66px;
  z-index: 1;
  border: none;
  border-radius: 60px;
  padding: 10px 20px 10px 20px;
  font-weight: bold;

  &: hover {
    background: #dbdbdb;
  }
`;

export default Location;
