/*global kakao*/

"use strict";
import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import imhere from "./assets/marker1.png";
import axios from "axios";
import { CopyToClipboard } from "react-copy-to-clipboard";

const Map = () => {
  const [latitude, setLatitude] = useState(null);
  const [longitude, setLongitude] = useState(null);
  const [status, setStatus] = useState(null);
  const [mymap, setMyMap] = useState([]);
  const resetRef = useRef(null);

  useEffect(() => {
    kakaoMap();
    onLocation();
    return () => {
      console.log("ì»´í¬ë„ŒíŠ¸ê°€ ì‚¬ë¼ì§.");
    };
  }, []);

  const kakaoMap = () => {
    //ì§€ë„ë¥¼ ë‹´ì€ maps
    let maps = document.getElementById("map");

    //ì§€ë„ option ì„¤ì •
    let options = {
      center: new kakao.maps.LatLng(33.450701, 126.570667),
      level: 2,
    };

    //ì§€ë„ ìƒì„±
    resetRef.current = new kakao.maps.Map(maps, options);
  };

  // eslint-disable-next-line
  const getLocation = async () => {
    if (!navigator.geolocation) {
      setStatus("í˜„ì¬ ë¸Œë¼ìš°ì €ì—ì„œ ìœ„ì¹˜ ì •ë³´ê°€ ì§€ì›ë˜ì§€ ì•ŠìŠµë‹ˆë‹¤.");
      return;
    }
    setStatus("ìœ„ì¹˜ ì°¾ëŠ”ì¤‘...");
    const position = await new Promise((resolve, reject) => {
      navigator.geolocation.getCurrentPosition(resolve, reject);
    });

    let lat = position.coords.latitude;
    let lon = position.coords.longitude;

    return {
      lon: position.coords.longitude,
      lat: position.coords.latitude,
    };
  };

  //ì¢Œí‘œ ìœ„ì¹˜ëŒ€ë¡œ ì§€ë„ ë³€ê²½
  const displayLoc = (latitude, longitude) => {
    const locPosition = new kakao.maps.LatLng(latitude, longitude);

    //ë§ˆì»¤ ì´ë¯¸ì§€
    let imageSrc = `${imhere}`,
      imageSize = new kakao.maps.Size(70, 90),
      imageOption = { offset: new kakao.maps.Point(27, 100) };

    let markerImage = new kakao.maps.MarkerImage(
        imageSrc,
        imageSize,
        imageOption
      ),
      markerPosition = new kakao.maps.LatLng(latitude, longitude);

    let marker = new kakao.maps.Marker({
      position: markerPosition,
      image: markerImage,
    });
    resetRef.current.setCenter(locPosition);
    marker.setMap(resetRef.current);
  };

  //ìœ„ì¹˜ ì°¾ê¸° ë²„íŠ¼
  const onLocation = async () => {
    let { lat, lon } = await getLocation();
    displayLoc(lat, lon);

    const response = await axios.get(
      `http://api.iam-here.site/location?x=${lon}&y=${lat}`
    );
    setMyMap(response.data.data);
  };

  return (
    <>
      <h3>
        í˜„ì¬ ë‚´ ìœ„ì¹˜ëŠ”...{mymap.roadAddress} ì…ë‹ˆë‹¤ğŸƒâ€â™€ï¸.
        <br />
      </h3>

      <Maps id="map" />
      <CopyToClipboard>
        <button>ê³µìœ í•˜ê¸°</button>
      </CopyToClipboard>
    </>
  );
};

const Maps = styled.div`
  width: 400px;
  height: 400px;
  border-radius: 25px;
  box-shadow: 3px 3px 15px #e6e6e6;
`;

const Button = styled.button`
  background: #e3e3e3;
  position: relative;
  bottom: 410px;
  left: 300px;
  z-index: 1;
  border: none;
  border-radius: 1em;
  padding: 10px 20px 10px 20px;
  font-weight: bold;
  box-shadow: 1px 1px 3px #e6e6e6;

  &: hover {
    background: #dbdbdb;
  }
`;

export default Map;
