import React, { useState } from "react";
import styled from "styled-components";
import Map from "./Map";
import { Link } from "react-router-dom";
import "./assets/Map.css";

const Location = () => {
  const [openMap, setOpenMap] = useState(false);

  // const onToggle = () => {
  //   setOpenMap(!openMap);
  // };
  return (
    <Background>
      <Container>
        <Title>
          <h5>here</h5>
          <svg
            height="1"
            width="249"
            style={{ backgroundColor: "#707070" }}
          ></svg>
        </Title>
        <h3>안녕하세요!</h3>
        <h2>내 위치를 찾아볼까요?</h2>
        <Emoji>💁</Emoji>
        <Button to="/map">Get started</Button>
        <Balloon></Balloon>
      </Container>
    </Background>
  );
};

const Background = styled.div`
  border: 1px solid red;
  width: 414px;
  height: 896px;
  margin: 30px;
`;

const Container = styled.div`
  margin: 40px;
`;

const Title = styled.div`
  margin-bottom: 40px;
  font-size: 30px;
  display: grid;
  grid-template-columns: 1fr 2fr;
  align-items: center;
`;

const Emoji = styled.div`
  font-size: 100px;
  margin-top: 30px;
  margin-bottom: 30px;
`;

const Button = styled(Link)`
  background: #44884b;
  width: 180px;
  height: 66px;
  z-index: 1;
  border: none;
  border-radius: 60px;
  padding: 15px 24px 15px 24px;
  text-decoration: none;
  color: white;
  font-size: 24px;

  &: hover {
    background: #dbdbdb;
  }
`;

const Balloon = styled.div`
  width: 180px;
  height: 100vh;
`;

export default Location;
