import React from "react";
import { CopyToClipboard } from "react-copy-to-clipboard";
import styled from "styled-components";
import icon_share from "./assets/hw-icon_share.png";

const Share = ({ mymap }) => {
  const shareText = `🎤 현재 위치는 ${mymap.roadAddress}입니다. 
  w3w주소 : ${mymap.w3w}
  위도 : ${mymap.location.latitude}
  경도 : ${mymap.location.longitude}
  카카오맵 : ${mymap.kakaoMap}`;
  return (
    <Container>
      <CopyToClipboard text={shareText}>
        <Button>내 현위치 복사</Button>
      </CopyToClipboard>
      <Button_share>=</Button_share>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  justify-content: center;
`;
const Button = styled.button`
  background: #000000;
  width: 323px;
  height: 60px;
  margin: 10px
  z-index: 1;
  border: none;
  border-radius: 15px;
  padding: 15px 24px 15px 24px;
  text-decoration: none;
  color: white;
  font-size: 20px;
  font-weight: bold;
  text-align: center;

  &: hover {
    background: #5ba562;
  }
`;

const Button_share = styled.button`
  width: 60px;
  height: 60px;
  border-radius: 15px;
  background: #d5d5d5;
  border: none;
  margin-left: 5px;
  content: ${icon_share};
`;

export default Share;
